operations.schema:
input SetGranteeGrantSizeInput {
	grantSize: Float!
	matchingGrantSize: Float!
}

input SetGranteeSummaryInput {
	grantSummary: String!
	fundingAddress: String!
	fundingType: [FundingType!]!
	disbursementContractAddress: String!
}

enum FundingType {
	EOA
	Multisig
	TwoofThreeMultisig
	ThreeofFiveMultisig
}

input SetGranteeMetricsDashInput {
    metricsDashboardLink: String!
}

# add new types here

input InitPhaseInput {
    startDate: DateTime!
	phaseDuration: Int 
    numberOfPhases: Int
}

scalar DateTime
